	exit;

// Generate Visual Confirm (start from 1)
function gen_visual($code, $length = 6)
{
	$imgstr = '';
	for ($i = 1; $i <= $length; $i++)
		$imgstr .= '<img src="module.php?eps=visual&amp;code='.$code.'&amp;pos='.$i.'" />';
	return '<span class="confirmimg">'.$imgstr.'</span>';
}

// Encrypt String (Complex :D)
function eps_encrypt($str, $maxlen = null)
{
	if (!$maxlen)
		$maxlen = strlen($str);
	//$chr = 'ABCDEFGHIJKLM0123456789NOPQRSTUVWXYZ';
	$chr = '1234567890';
	$hash = md5($str);
	$enc = '';
	for ($i = 0; $i < $maxlen; $i++)
		$enc .= substr($chr, (ord(substr($hash, ($i + 3) % strlen($hash), 1)) * 21 % strlen($chr)), 1);
	return $enc;
}

